# Quest 1
# 11/15/2024


def solve1(input):
    result = 0
    for char in input:
        if char == "B":
            result += 1
        elif char == "C":
            result += 3

    return result


def solve2(input):
    result = 0
    size = len(input)
    for i in range(0, size, 2):
        firstChar = input[i]
        secondChar = input[i + 1]
        if firstChar == "B":
            result += 1
        elif firstChar == "C":
            result += 3
        elif firstChar == "D":
            result += 5
        if secondChar == "B":
            result += 1
        elif secondChar == "C":
            result += 3
        elif secondChar == "D":
            result += 5
        if firstChar != "x" and secondChar != "x":
            result += 2

    return result


def solve3(input):
    result = 0
    size = len(input)
    for i in range(0, size, 3):
        firstChar = input[i]
        secondChar = input[i + 1]
        thirdChar = input[i + 2]
        numEnemies = 0
        if firstChar == "B":
            result += 1
        elif firstChar == "C":
            result += 3
        elif firstChar == "D":
            result += 5
        if secondChar == "B":
            result += 1
        elif secondChar == "C":
            result += 3
        elif secondChar == "D":
            result += 5
        if thirdChar == "B":
            result += 1
        elif thirdChar == "C":
            result += 3
        elif thirdChar == "D":
            result += 5

        if firstChar != "x":
            numEnemies += 1
        if secondChar != "x":
            numEnemies += 1
        if thirdChar != "x":
            numEnemies += 1
        result += numEnemies * (numEnemies - 1)
    return result


def main():
    input = ""
    print("Part 1 answer:")
    print(solve1(input))
    input = ""
    print("Part 2 answer:")
    print(solve2(input))
    input = ""
    print("Part 3 answer:")
    print(solve3(input))


if __name__ == "__main__":
    main()
